mapper.select:
select * from blog_subject where 1 = 1 
    and blogSubjectId = #{blogSubjectId}  
    and subjectTitle = #{subjectTitle}  
    and blogTypeId = #{blogTypeId}  
    ${condition}

select * from blog_subject where 1 = 1 
    and blogSubjectId = #{blogSubjectId}  
    and subjectTitle = #{subjectTitle}  
    and blogTypeId = #{blogTypeId}  
    ${condition}  
    limit #{startPage},#{page}

select count(blogSubjectId) from blog_subject where 1 = 1 
    and blogSubjectId = #{blogSubjectId}  
    and subjectTitle = #{subjectTitle}  
    and blogTypeId = #{blogTypeId}  
    ${condition}

mapper.delete:
delete from blog_subject where 1 = 1 
    and blogSubjectId = #{blogSubjectId}  
    and subjectTitle = #{subjectTitle}  
    and blogTypeId = #{blogTypeId}  
    ${condition}
