mapper.select:
select * from user where 1 = 1 
    and userId = #{userId}  
    and username = #{username}  
    and password = #{password}  
    ${condition}

select * from user where 1 = 1 
    and userId = #{userId}  
    and username = #{username}  
    and password = #{password}  
    ${condition}  
    limit #{startPage},#{page}

select count(userId) from user where 1 = 1 
    and userId = #{userId}  
    and username = #{username}  
    and password = #{password}  
    ${condition}

mapper.delete:
delete from user where 1 = 1 
    and userId = #{userId}  
    and username = #{username}  
    and password = #{password}  
    ${condition}
